mapper.select:
SELECT m.* FROM sys_menu m
INNER JOIN sys_role_menu rm ON m.id = rm.menu_id AND rm.role_id = #{roleid}
WHERE m.isvalid = 1 AND m.isshow = 1 AND m.parent_id = #{parentid} ORDER BY m.sort DESC, m.updatetime DESC, m.createtime DESC

select a.*,p.name as parentname from (select t.*, connect_by_root(t.id), connect_by_root(t.name) from sys_menu t where t.isvalid = 1
and systemcode=#{systemcode}
start with t.parent_id = '0' connect by t.parent_id = prior t.id) a left join sys_menu p on a.parent_id=p.id order by a.leve,a.sort asc
